const starsCanvas = document.getElementById("stars"), starsCtx = starsCanvas.getContext("2d"); let stars = []; const numStars = 100; function resizeStarsCanvas() { starsCanvas.width = window.innerWidth, starsCanvas.height = window.innerHeight } function createStars() { stars = []; for (let a = 0; a < 100; a++)stars.push({ x: Math.random() * starsCanvas.width, y: Math.random() * starsCanvas.height, radius: 1.5 * Math.random(), speed: .5 * Math.random() + .2 }) } function drawStars() { for (let a of (starsCtx.clearRect(0, 0, starsCanvas.width, starsCanvas.height), starsCtx.fillStyle = "#ffccff", stars)) starsCtx.beginPath(), starsCtx.arc(a.x, a.y, a.radius, 0, 2 * Math.PI), starsCtx.fill() } function updateStars() { for (let a of stars) a.y += a.speed, a.y > starsCanvas.height && (a.y = 0, a.x = Math.random() * starsCanvas.width) } function animateStars() { drawStars(), updateStars(), requestAnimationFrame(animateStars) } window.addEventListener("resize", () => { resizeStarsCanvas(), createStars() }), resizeStarsCanvas(), createStars(), animateStars();